const cursor=()=>{gsap.set(".pointed",{xPercent:-50,yPercent:-50});const e=document.querySelector(".pointed"),t={x:window.innerWidth/2,y:window.innerHeight/2},o={x:t.x,y:t.y},s=gsap.quickSetter(e,"x","px"),c=gsap.quickSetter(e,"y","px");window.addEventListener("mousemove",e=>{o.x=e.x,o.y=e.y}),gsap.ticker.add(()=>{const e=1-Math.pow(.8,gsap.ticker.deltaRatio());t.x+=(o.x-t.x)*e,t.y+=(o.y-t.y)*e,s(t.x),c(t.y)})};cursor();const hoverServices=()=>{const e=document.querySelectorAll(".servicios__item");for(const t of e)t.onmouseover=()=>{for(const t of e)t.classList.remove("active","onactive");t.classList.add("active")}};hoverServices();